import React, {Component} from 'react';
import { Header, Input, Button } from 'semantic-ui-react';

import AceEditor from "react-ace";

import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/theme-terminal";

import socket from '../socket';
import { useEffect } from "react";


// function App() {
class App extends Component {
  // useEffect(() => {
  //   socket.emit('hello', 'erewrewr');
  //   socket.on('privet', () => {
  //     console.log('privet!!!')
  //   })
  // }, []);
  constructor(props) {
    super(props);
    this.state = {
      valiue: ''
    };
  }

  handleChange(value) {
    socket.emit('CHANGE_CLIENT', value);
    this.setState({
      value
    });
  }

  componentDidMount() {
    socket.on('CHANGE_SERVER', value => {
      this.setState({
        value
      });
    });
  }

  connectRomm() {
    socket.emit('CHANGE_CLIENT', this.input.value);
  }
  render() {
  return (
    <div>
      <div className="header">
        <Header as='h1'>KISO</Header>
        <Input value={Math.round(Math.random() * 1000)}ref={ref => (this.input = ref)} />
        <Button>Connect</Button>
      </div>
      <div className="editor">
      <AceEditor
        mode="javascript"
        theme="terminal"
        name="editor"
        fontSize={14}
        showGutter={true}
        highlightActiveLine={true}
        editorProps={{ $blockScrolling: true }}
        value={this.state.value}
        onChange={this.handleChange.bind(this)}
      />
      </div>
    </div>
  );
}
}

export default App;
